#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
    int findMaxConsecutiveOnes(vector<int>& arr) {
        int count= 0;
        int MaxCount = 0;
        for (auto it = arr.begin(); it != arr.end(); it++)
        {
            if(*it == 1){
                count++;
            }
            else{
                MaxCount = max(count,MaxCount);
                count = 0;
            }
        }
        cout<<max(count,MaxCount);
        return(max(count,MaxCount));
        
    }
};


